export type AtomicSwapConfig = {
    secretHash: string;
    expiryBlocks: number;
    /**
     * The number of blocks before the swap expires
     */
    amount: number;
    /**
     * Amount in it's lowest denomination
     */
    recipientAddress: string;
    refundAddress: string;
    initiatorAddress: string;
    contractAddress?: string;
    chain?: number;
};
